import sofia.micro.*;//-------------------------------------------------------------------------/** *  Weapon fired at asteroids to destroy/remove them. *  *  @author jackrg94 *  @version 2015.04.13 */public class Bullet extends SpaceObjects{    //~ Fields ................................................................    //~ Constructor ...........................................................    // ----------------------------------------------------------    /**     * Creates a new Bullet object.     * @param s    int for how fast object moves in pixles per turn     * @param r  which way the object is oriented. in degrees     */    public Bullet(int s, int r)    {        super(s, r);    }    //~ Methods ...............................................................    /**     * moves the bullet each turn based on the speed     */    public void act()    {        this.move();    }    /**     * overrides move to remove bullet if it leaves the world     */    public void move()    {        if ((getGridX() <= 1) || (getGridX() >= 499)             || (getGridY() <= 1) || (getGridY() >= 499))        {            getWorld().remove(this);        }        else        {            super.move();        }    }        /**     * overrides collide to exempt ships     */    public void collide()    {        if (this.getOneIntersectingObject(Ship.class) == null            && this.getOneIntersectingObject(Bullet.class) == null)        {            super.collide();        }    }}